    static <T extends Block> T registerBlock(String id, T block) {
        BLOCKS.put(new Identifier(MODID, id), block);
        return block;
    }
    
    static void init() {
        BLOCKS.forEach((id, block) -> Registry.register(Registry.BLOCK, id, block));
    }
